mapper.insert:
 		INSERT INTO tbl_file (
 			file_num, 	file_original, 	file_upname
 		) VALUES (
	 		#{file_num},	#{file_original},	#{file_upname}
 		)

 		INSERT INTO tbl_file (
 			file_num, content_num, 	file_original, 	file_upname
 		) VALUES (
	 		#{file_num}, #{content_num},	#{file_original},	#{file_upname}
 		)
 		ON DUPLICATE KEY UPDATE
 			file_num = #{file_num},
 			file_originname = #{file_original},
 			file_upname = #{file_upname}
